const generateRandomId = () =>
  Math.random().toString(36).substring(2, 8) +
  Date.now().toString().substring(9);

const capitalizeFirstLetter = str => str[0].toUpperCase() + str.slice(1).toLowerCase();

const calculateAge = (dateBirth) => {
  const [day, month, year] = dateBirth.split('.').map(Number);
  const birthDate = new Date(year, month - 1, day);;
  const today = new Date();
  let age = today.getFullYear() - birthDate.getFullYear();
  const monthDiff = today.getMonth() - birthDate.getMonth();
  const dayDiff = today.getDate() - birthDate.getDate();

  if (monthDiff < 0 || (monthDiff === 0 && dayDiff < 0)) {
    age--;
  }
  return age.toString();
}

const transformData = ({ name, dateOfBirth, purpose }) => {
  const [firstName, lastName] = name.split(' ').map(capitalizeFirstLetter);

  const transformedData = {
    id: generateRandomId(),
    firstName,
    lastName,
    dateOfBirth,
    age: calculateAge(dateOfBirth),
    purpose: capitalizeFirstLetter(purpose),
  };

  return transformedData;
}

module.exports = transformData;